	private static String genFormatCode() {
		return randFromArray(colorCodes) + randFromArray(markupCodes);
	}
	
	public static String getWords(int minWords, int maxWords, int maxLength) {
		int numWords = random(minWords, maxWords);
		
		String[] chosen = new String[numWords];
		
		int length = 0;
		
		for (int i = 0; i < numWords; ++i) {
			/* grab a random word */
			//StringBuilder temp = new StringBuilder(randFromArray(randFromArray(words)));
			StringBuilder temp = new StringBuilder(randFromArray(bookWords));
			
			/* corrupt the word slightly */
			if (Math.random() < 0.05f)
				temp.setCharAt(random(0, temp.length() - 1), (char)random(' ', '~'));
			
			/* add separator to the end of word */
			if (i != numWords - 1)
				temp.append((Math.random() < 0.95f) ? " " : "");
			
			String string = temp.toString();
			
			//length += Minecraft.getInstance().fontRenderer.getStringWidth(string);
			
			/* place it into list */
			chosen[i] = string;
		}
		
		/* overflow strings get set to empty string */
		if (length > maxLength)
			return "";
		
		StringBuilder ret = new StringBuilder();
		
		for (int i = 0; i < numWords; ++i) {
			ret.append(genFormatCode());
			ret.append(chosen[i]);
		}
		
		return ret.toString();
	}
